import { createContext, ReactNode, useContext, useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import { ProductList } from '../pages/Home/styles';
import { api } from '../services/api';
import { Product, Stock } from '../types';

interface CartProviderProps {
  children: ReactNode;
}

interface UpdateProductAmount {
  productId: number;
  amount: number;
}

interface CartContextData {
  cart: Product[];
  addProduct: (productId: number) => Promise<void>;
  removeProduct: (productId: number) => void;
  updateProductAmount: ({ productId, amount }: UpdateProductAmount) => void;
}

const CartContext = createContext<CartContextData>({} as CartContextData);

export function CartProvider({ children }: CartProviderProps): JSX.Element {
  const [cart, setCart] = useState<Product[]>(() => {
    const storagedCart = localStorage.getItem('@RocketShoes:cart')

    if (storagedCart) {
      return JSON.parse(storagedCart);
    }

    return [];
  });

  const addProduct = async (productId: number) => {
    try {
        const productAlreadyInCart = cart.find(product => product.id === productId);

        if(!productAlreadyInCart){
          const {data: product} = await api.get<Product>(`products/${productId}`)
          const {data: stock} = await api.get<Stock>(`stock/${productId}`)

          if(stock.amount > 0){
            setCart([...cart, {...product, amount:1}])
            localStorage.setItem('@RocketShoes:cart',JSON.stringify([...cart,{...product, amount:1}]))
            return;
          }else{
            toast.error('Quantidade solicitada fora de estoque')
            return;
          }
        }

        if(productAlreadyInCart){
          const {data:stock} = await api.get(`stock/${productId}`)

          if(stock.amount <= productAlreadyInCart.amount){
            toast.error('Quantidade solicitada fora de estoque')
            return;
          }

          const updateCart = cart.map(cartItem => cartItem.id === productId ? {
            ...cartItem,
            amount: Number(cartItem.amount) + 1
          } : cartItem)

          setCart(updateCart)
          localStorage.setItem('@RocketShoes:cart', JSON.stringify(updateCart))

          return;

        }


      // const data = await api.get(`products/${productId}`)      
      // setCart([...cart,data.data]);

    } catch {

      toast.error('Erro na adição do produto')
    }
  };

  const removeProduct = (productId: number) => {
    try {
     const productExists = cart.some(cartProduct => cartProduct.id === productId);
     if(!productExists){
       toast.error('Erro na remoção do produto')
       return
     }

     const updatedCart = cart.filter(cartItem => cartItem.id !== productId)
     setCart(updatedCart)
     localStorage.setItem('@RocketShoes:cart',JSON.stringify(updatedCart))
    } catch {
      toast.error('Erro na remoção do produto')
    }
  };

  // const updatedAmountCartProduct = cart.map(product => {
  //   return product.id === productId 
  //     ? {...product, amount: productAmount + 1 } 
  //     : product;
  // });

  const updateProductAmount = async ({
    productId,
    amount,
  }: UpdateProductAmount) => {
    try {
      if(amount < 1){
        toast.error('Erro na alteração de quantidade do produto')
        return
      }

      const response = await api.get(`/stock/${productId}`)
      const productAmount = response.data.amount 
      const stockIsNotAvailable = productAmount <= amount

      if(stockIsNotAvailable){
        toast.error('Quantidade solicitada fora de estoque')
        return
      }
      const productExists = cart.some(cartProduct => cartProduct.id === productId);
      if(!productExists){
       toast.error('Erro na alteração de quantidade do produto')
       return
     }

     const updatedCart = cart.map(cartItem => cartItem.id === productId ? {
       ...cartItem,
       amount: amount
     }: cartItem)
     setCart(updatedCart)
     localStorage.setItem('@RocketShoes:cart',JSON.stringify(updatedCart))
    } catch {
      toast.error('Erro na alteração da quantidade do produto')
    }
  };

  return (
    <CartContext.Provider
      value={{ cart, addProduct, removeProduct, updateProductAmount }}
    >
      {children}
    </CartContext.Provider>
  );
}

export function useCart(): CartContextData {
  const context = useContext(CartContext);

  return context;
}
